import React, { useState } from 'react';
import axios from 'axios';
import { pinyin } from 'pinyin-pro';

const zhuyin2keyboard = {
  'ㄅ': '1', 'ㄆ': 'q', 'ㄇ': 'a', 'ㄈ': 'z',
  'ㄉ': '2', 'ㄊ': 'w', 'ㄋ': 's', 'ㄌ': 'x',
  'ㄍ': 'e', 'ㄎ': 'd', 'ㄏ': 'c',
  'ㄐ': 'r', 'ㄑ': 'f', 'ㄒ': 'v',
  'ㄓ': '5', 'ㄔ': 't', 'ㄕ': 'g', 'ㄖ': 'b',
  'ㄗ': 'y', 'ㄘ': 'h', 'ㄙ': 'n',
  'ㄧ': 'u', 'ㄨ': 'j', 'ㄩ': 'm',
  'ㄚ': '8', 'ㄛ': 'i', 'ㄜ': 'k', 'ㄝ': ',',
  'ㄞ': '9', 'ㄟ': 'o', 'ㄠ': 'l', 'ㄡ': '.',
  'ㄢ': '0', 'ㄣ': 'p', 'ㄤ': ';', 'ㄥ': '/',
  'ㄦ': '-',
  'ˊ': '6', 'ˇ': '3', 'ˋ': '4', '˙': '7'
};

const translateAPI = async (text) => {
  const apiKey = 'AIzaSyCAxoFUMU9yZNjUjHDTl8d41m6J7P7MSGg'; // 替換為你的 API 密鑰
  const url = `https://translation.googleapis.com/language/translate/v2?key=${apiKey}`;
  try {
    const response = await axios.post(url, {
      q: text,
      target: 'zh-TW', // 目標語言設置為繁體中文
      format: 'text'
    });
    return response.data.data.translations[0].translatedText;
  } catch (error) {
    console.error('Error translating text:', error);
    throw error;
  }
};

const chinese2zhuyin = (text) => {
  return pinyin(text, { type: 'zhuyin', toneType: 'symbol', v: true });
};

const zhuyin2keyboard2 = (zhuyin) => {
  return zhuyin.split('').map(char => zhuyin2keyboard[char] || char).join('');
};

const TranslationEncryption = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');

  const handleTranslate = async () => {
    try {
      const chinese = await translateAPI(input);
      const zhuyin = chinese2zhuyin(chinese);
      const encrypted = zhuyin2keyboard2(zhuyin);
      setResult(`中文：${chinese}\n注音：${zhuyin}\n加密：${encrypted}`);
    } catch (error) {
      setResult('轉換過程中出現錯誤');
    }
  };

  return (
    <div className="p-4 bg-gray-100 rounded-lg">
      <h2 className="text-2xl font-bold mb-4">翻譯加密器</h2>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="w-full p-2 mb-4 border rounded"
        placeholder="輸入英文單詞"
      />
      <button
        onClick={handleTranslate}
        className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
      >
        翻譯並加密
      </button>
      <pre className="mt-4 p-2 bg-white rounded">{result}</pre>
    </div>
  );
};

export default TranslationEncryption;
